    padding: 60px 0;
    text-align: center;
}

.sponsors-section h2 {
    margin-bottom: 20px;
    color: var(--primary-color);
    font-weight: 600;
}

.section-subtitle {
    margin-bottom: 40px;
    color: var(--text-secondary);
}

.sponsors-grid {
    margin-top: 30px;
}

.sponsor-card {
    display: block;
    padding: 20px;
    background-color: var(--card-bg);
    border-radius: 12px;
    transition: all 0.3s ease;
    text-align: center;
    text-decoration: none;
    height: 100%;
    box-shadow: 0 4px 15px rgba(0, 0, 0, 0.1);
}

.sponsor-card:hover {
    transform: translateY(-5px);
    box-shadow: 0 10px 25px rgba(0, 0, 0, 0.15);
}

.sponsor-logo {
    width: 100%;
    height: 120px;
    display: flex;
    align-items: center;
    justify-content: center;
    margin-bottom: 15px;
}

.sponsor-logo img {
    max-width: 80%;
    max-height: 80px;
    object-fit: contain;
}

.sponsor-name {
    margin: 0;
    color: var(--text-color);
    font-size: 1rem;
    font-weight: 500;
}

@media (max-width: 768px) {
    .sponsor-logo {
        height: 100px;
    }
    
    .sponsor-logo img {
        max-height: 60px;
    }
}

/* Değerler Listesi Stillemesi */
.values-list {
    list-style: none;
    padding: 0;
    margin: 0;
}

.values-list li {
    padding: 12px 0;
    border-bottom: 1px solid rgba(255, 255, 255, 0.1);
}

.values-list li:last-child {
    border-bottom: none;
}

.values-list strong {
    color: var(--primary-color);
    margin-right: 5px;
}

/* Genel Bölüm Stillemesi */
.about-section {
    padding: 60px 0;
    border-bottom: 1px solid rgba(255, 255, 255, 0.1);
}

.about-section:last-of-type {
    border-bottom: none;
}

.about-content h2 {
    margin-bottom: 20px;
    color: var(--primary-color);
}

.about-image img {
    width: 100%;
    border-radius: 12px;
    box-shadow: 0 10px 30px rgba(0, 0, 0, 0.15);
}

.about-hero {
    background: linear-gradient(135deg, var(--primary-color), var(--secondary-color));
    padding: 80px 0;
    text-align: center;
    margin-bottom: 30px;
    border-radius: 0 0 50px 50px;
}

.about-hero h1 {
    color: white;
    font-size: 2.5rem;
    margin-bottom: 15px;
}

.about-subtitle {
    color: rgba(255, 255, 255, 0.9);
    font-size: 1.2rem;
    max-width: 600px;
    margin: 0 auto;
}
