<script src="https://cdn.jsdelivr.net/npm/chart.js"></script>
<script>
// Mining Stats Chart
document.addEventListener('DOMContentLoaded', function() {
    const ctx = document.getElementById('miningStatsChart');
    if (!ctx) return;
    
    // Get mining stats data
    fetch('ajax/mining_stats.php')
    .then(response => response.json())
    .then(data => {
        new Chart(ctx, {
            type: 'line',
            data: {
                labels: data.dates,
                datasets: [
                    {
                        label: 'Mining Revenue',
                        data: data.earnings,
                        borderColor: '#20c997',
                        backgroundColor: 'rgba(32, 201, 151, 0.1)',
                        tension: 0.4,
                        fill: true
                    },
                    {
                        label: 'Electricity Cost',
                        data: data.costs,
                        borderColor: '#ea5455',
                        backgroundColor: 'rgba(234, 84, 85, 0.1)',
                        tension: 0.4,
                        fill: true
                    },
                    {
                        label: 'Net Profit',
                        data: data.net_earnings,
                        borderColor: '#7367f0',
                        backgroundColor: 'rgba(115, 103, 240, 0.1)',
                        tension: 0.4,
                        fill: true
                    }
                ]
            },
            options: {
                responsive: true,
                interaction: {
                    mode: 'index',
                    intersect: false,
                },
                plugins: {
                    legend: {
                        position: 'top',
                        labels: {
                            usePointStyle: true,
                            boxWidth: 8,
                            padding: 15
                        }
                    },
                    tooltip: {
                        mode: 'index',
                        intersect: false
                    }
                },
                scales: {
                    y: {
                        beginAtZero: true,
                        ticks: {
                            callback: function(value) {
                                return value.toFixed(6) + ' USDT';
                            }
                        }
                    },
                    x: {
                        ticks: {
                            maxRotation: 0
                        }
                    }
                },
                elements: {
                    point: {
                        radius: 0,
                        hoverRadius: 5
                    }
                }
            }
        });
    })
    .catch(error => {
        console.error('Error fetching mining stats:', error);
    });
});

// Toggle mining status
function toggleMiningStatus(packageId, action) {
    if (!confirm(action === 'pause' ? 
                'Are you sure you want to pause this mining package?' : 
                'Are you sure you want to resume this mining package?')) {
        return;
    }
    
    fetch('../ajax/toggle_mining.php', {
        method: 'POST',
        headers: {
            'Content-Type': 'application/x-www-form-urlencoded',
        },
        body: 'package_id=' + packageId + '&action=' + action
    })
    .then(response => response.json())
    .then(data => {
        if (data.status === 'success') {
            alert(action === 'pause' ? 'Mining paused successfully!' : 'Mining resumed successfully!');
            window.location.reload();
        } else {
            alert('Error: ' + data.message);
        }
    })
    .catch(error => {
        console.error('Error changing mining status:', error);
        alert('An error occurred. Please try again later.');
    });
}
</script>

